using System.Collections.ObjectModel;
using SecretaryDesktopApp.Models.DTO;

namespace SecretaryDesktopApp.ViewModels;

public class MainWindowViewModel:NotifyBase
{
    public MainWindowViewModel()
    {
        _pages = new ObservableCollection<PageBaseViewModel>();
        _pages.Add(new ExcelLoaderViewModel());
        User = new UserModel()
        {
            Avatar =
                "",
            FullName = "Пидор пидорович",
            Username = "loh@mail.ru"
        };
    }

    private ObservableCollection<PageBaseViewModel> _pages;

    public ObservableCollection<PageBaseViewModel> Pages
    {
        get => _pages;
        set => Update(ref _pages, value);
    }

    private PageBaseViewModel _selectedPage;

    public PageBaseViewModel SelectedPage
    {
        get => _selectedPage;
        set => Update(ref _selectedPage, value); 
    }

    private UserModel _user;

    public UserModel User
    {
        get => _user;
        set => Update(ref _user, value);
    }

}